AOS.init({duration:1000,once:true,offset:100});document.querySelectorAll('a[href^="#"]').forEach(anchor=>{anchor.addEventListener('click',function(e){e.preventDefault();document.querySelector(this.getAttribute('href')).scrollIntoView({behavior:'smooth'});});});function openModal(imageSrc){const modal=document.getElementById('imageModal');const modalImg=document.getElementById('modalImage');modal.style.display='block';modalImg.src=imageSrc;setTimeout(()=>modal.classList.add('show'),10);}
function closeModal(){const modal=document.getElementById('imageModal');modal.classList.remove('show');setTimeout(()=>modal.style.display='none',300);}
window.addEventListener('scroll',()=>{const scrolled=window.pageYOffset;const parallaxElements=document.querySelectorAll('.parallax');parallaxElements.forEach(el=>{const speed=el.dataset.speed||0.5;el.style.transform=`translateY(${scrolled * speed}px)`;});});function animateValue(obj,start,end,duration){let startTimestamp=null;const step=(timestamp)=>{if(!startTimestamp)startTimestamp=timestamp;const progress=Math.min((timestamp-startTimestamp)/duration,1);obj.innerHTML=Math.floor(progress*(end-start)+start);if(progress<1){window.requestAnimationFrame(step);}};window.requestAnimationFrame(step);}
const observer=new IntersectionObserver((entries)=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.classList.add('animate-fade-in-up');if(entry.target.classList.contains('animate-number')){const number=parseInt(entry.target.dataset.number);animateValue(entry.target,0,number,2000);}}});},{threshold:0.1});document.querySelectorAll('section > div > *, .animate-number').forEach((el)=>{observer.observe(el);});document.querySelectorAll('.feature-card').forEach(card=>{card.addEventListener('mouseenter',()=>{card.classList.add('hover:shadow-2xl');});card.addEventListener('mouseleave',()=>{card.classList.remove('hover:shadow-2xl');});});;